var popup=document.querySelector(".modal-content"),link=document.querySelector(".login"),overlay=document.querySelector(".modal-overlay"),btnClose=popup.querySelector(".modal-content-close"),form=popup.querySelector(".login-form"),login=popup.querySelector("[name=login]"),password=popup.querySelector("[name=password]");form.addEventListener("submit",function(o){login.value&&password.value||(o.preventDefault(),popup.classList.add("modal-error"))}),link.addEventListener("click",function(o){o.preventDefault(),popup.classList.add("modal-content-show"),overlay.classList.add("modal-overlay-show")}),btnClose.addEventListener("click",function(o){o.preventDefault(),popup.classList.remove("modal-content-show"),popup.classList.remove("modal-error"),overlay.classList.remove("modal-overlay-show")}),window.addEventListener("keydown",function(o){27===o.keyCode&&popup.classList.contains("modal-content-show")&&(popup.classList.remove("modal-content-show"),popup.classList.remove("modal-error"),overlay.classList.remove("modal-overlay-show"))});